import axios from 'axios'
import {useState, useEffect} from 'react'
import { useNavigate } from 'react-router-dom'
import '../styles/RegisterAcademy.css'
import AcademyCardList from './AcademyCardList'

const SelectFromList = () => {

    const api = "http://localhost:8088/api/v1/academy/"
    const navigate = useNavigate()
    const [name, setName] = useState("")
    const [address, setAddress] = useState("")
    const [invalidName, setInvalidName] = useState(false)
    const [invalidAddress, setInvalidAddress] = useState(false)


    const ListAcademy = () => {

        const api = "http://localhost:8088/api/v1/academy/"
        const [academyList, setAcademyList] = useState([])
    
        useEffect(() => {
            loadAcademies()
        }, [])
    
        const loadAcademies = () => {
            axios.get(api)
                    .then(response => {setAcademyList(response.data)})
                    .catch(error => console.log('unable to load data'))
        }
    
        return(
            <div>
                {
                    academyList.map(
                        academy => <AcademyCardList key={academy.id} academy={academy}/>
                    )
                }
               <br/>
               
            </div>
        )
    }
   

    
}

export default SelectFromList
