import { useState } from "react"
import '../styles/RegisterProducts.css'

const RegisterProduct = () => {
    const [name, setName] = useState("")
    const [description, setDescription] = useState("")
    const [price, setPrice] = useState("")
    const [invalidName,setInvalidName] = useState(false)
    const [invalidDescription,setInvalidDescription] = useState(false)
    const [invalidPrice,setInvalidPrice] = useState(false)


    const handleSubmit = (event) => {
        event.preventDefault()
        if (name === ""){
            setInvalidName(true)
        } else {
            setInvalidName(false)
        }

        if (description === ""){
            setInvalidDescription(true)
        } else {
            setInvalidDescription(false)
        }

        if (price < 0){
            setInvalidPrice(true)
        } else {
            setInvalidPrice(false)
        }
    }

    return (
        <div className = 'productForm'>
            <h1>Enter product details:</h1>
            <form onSubmit={handleSubmit}>
                <label>Name:        </label>
                <input type="text" name="name" value={name} onChange={event => setName(event.target.value)}/>
                {invalidName && <span className="error">Name cannot be blank</span>}<br/>
                <label>Description:</label>
                <input type="text" name="description" value={description} onChange={event => setDescription(event.target.value)}/>
                {invalidDescription && <span className="error">Description cannot be blank</span>}<br/>
                <label>Price:</label>
                <input type="number" name="price" value={price} onChange={event => setPrice(event.target.value)}/>
                {invalidPrice && <span className="error">Price cannot be negative</span>}<br/>
                <button>Submit</button>
            </form>
        </div>

    )
}
export default RegisterProduct
