import {useState, useEffect} from 'react'
import ItemCard from './ItemCard'
import axios from 'axios'
import { useParams } from 'react-router-dom';

import { useNavigate } from 'react-router-dom'



const ListSingularAuction = () => {

    const api = "http://localhost:8081/auction/api/v1/item"
    const [itemList, setItemList] = useState([])
    const navigate = useNavigate()
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [bidAmount, setBidAmount] = useState("")
    const [invalidUsername, setInvalidUsername] = useState(false)
    const [invalidPassword, setInvalidPassword] = useState(false)
    const [invalidBidAmount, setInvalidBidAmount] = useState(false)

    const  params = useParams();

    useEffect(() => {
        loadItems()
    }, [])

    const loadItems = () => {
        console.log("THE ID USING PARAMS IS: " + params.item_id)
        axios.get(api + '/' + params.item_id)
                .then(response => {setItemList(response.data)})
                .catch(error => console.log('unable to load data'))
    }

    const handleSubmit = (event) => {
        event.preventDefault()
        if (username === "" && password === ""){
            setInvalidUsername(true)
            setInvalidPassword(true)
            setInvalidBidAmount(true)
        } else if(username == "") {
            setInvalidUsername(true) 
        } else if (password == ""){
            setInvalidPassword(true)
        } else if (bidAmount == ""){
            setInvalidBidAmount(true)
        } 
        
        else{
            axios.post(api,
                {
                   username : username,
                   password : password,
                   bidAmount : bidAmount,
                   name:itemList.name,
                   description:itemList.description,
                   price:itemList.price,
                   
                })
                .then(navigate('/page1'))

        }
    }





    return(
        <div>
        <div className="itemCard">
            <div className="itemDetails">
               <span>Name: {itemList.name}</span><br/>
               <span>Description: {itemList.description}</span><br/>
               <span>Price: Â£{itemList.price}</span><br/>
            </div>
            </div>
       <br/>
       <div className="bidForm">
            <br/>
            <h2> Bid on this item!</h2>
            <br/>
            <form onSubmit={handleSubmit}>
                <div class="form-group" >
                    <label for="username">Username</label>
                    <input type="text" class="form-control" value={username} onChange={event => setUsername(event.target.value)}  placeholder="Enter username"/>
                    {invalidUsername && <span className="error">Username cannot be blank</span>}<br/>
                </div>
                <div class="form-group">
                    <label for="password">Password</label>
                    <input type="password" class="form-control" placeholder="Password" name="password" value={password} onChange={event => setPassword(event.target.value)} />
                    {invalidPassword && <span className="error">Password cannot be blank</span>}<br />
                </div>
                <div class="form-group">
                    <label for="bidAmount">Bid Amount</label>
                    <input type="number" class="form-control" placeholder="Amount" name="Amount" value={itemList.price} onChange={event => setBidAmount(event.target.value)} />
                    {invalidBidAmount && <span className="error">Bid Amount cannot be blank</span>}<br />
                </div>
                    <button type="submit" class="btn btn-primary">Submit</button>
            </form>
        </div>
    </div>
    )
}

export default ListSingularAuction
