import {useState, useEffect} from 'react'
import ExamCard from './ExamCard'
import axios from 'axios'
import { useParams } from 'react-router-dom';


const ListExams = () => {

    const api = "http://localhost:8088/api/v1/examResult/trainee"
    
    const [examResults, setExamResults] = useState([])

    const  params = useParams();
    const [firstName, setFirstName] = useState([examResults.trainee.name])
    

    useEffect(() => {
        listExams()
    }, [])

    const listExams = () => {
        console.log("THE ID USING PARAMS IS: " + params.trainee_id)
        axios.get(api + '/' + params.trainee_id)
                .then(response => {setExamResults(response.data)})
                .catch(error => console.log('unable to load data'))
    }

    return(
      <div>

          <h1> Exam Results </h1> 
          <h2>{firstName}</h2>
          
          {
              examResults.map(
                  examResults => <ExamCard key={examResults.id} examResults={examResults}/>
              )
          }
         
      </div>
  )
}

export default ListExams
