import {useState, useEffect} from 'react'
import AcademyCard2 from './AcademyCard2'
import axios from 'axios'
import {Link} from 'react-router-dom'


const Academy2 = () => {

    const api = "http://localhost:8088/api/v1/academy/"
    const [academyList, setAcademyList] = useState([])

    useEffect(() => {
        loadAcademies()
    }, [])

    const loadAcademies = () => {
        axios.get(api)
                .then(response => {setAcademyList(response.data)})
                .catch(error => console.log('unable to load data'))
    }

    return(
        <div>
            {
                academyList.map(
                    academy => <AcademyCard2 key={academy.id} academy={academy}/>
                )
            }
           <br/><Link to="/register">Add a new academy</Link>
        </div>
    )
}

export default Academy2
