import AcademyCard from './AcademyCard'
import axios from 'axios'



const ListAcademys = () => {

    const api = "http://localhost:8088/api/v1/academy/"
    const [academyList, setAcademyList] = useState([])

    useEffect(() => {
        loadAcademys()
    }, [])

    const loadAcademys = () => {
        axios.get(api)
                .then(response => {setAcademyList(response.data)})
                .catch(error => console.log('unable to load data'))
    }

    return(
        <div>
            {
                academyList.map(
                    academy => <AcademyCard key={academy.id} academy={academy}/>
                )
            }
           
        </div>
    )
}

export default ListAcademys
