import axios from 'axios'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import '../styles/LoginPage.css'

const RegisterAcademy = () => {

    const api = "http://localhost:8088/api/v1/academy/"
    const navigate = useNavigate()
    const [name, setName] = useState("")
    const [address, setAddress] = useState("")
    const [invalidName, setInvalidName] = useState(false)
    const [invalidAddress, setInvalidAddress] = useState(false)



    const handleSubmit = (event) => {
        event.preventDefault()
        if (name === "" && address === ""){
            setInvalidName(true)
            setInvalidAddress(true)
        } else if(name == "") {
            setInvalidName(true) 
        } else if (address == ""){
            setInvalidAddress(true)
        } else{
            axios.post(api,
                {
                   name : name,
                   address : address,
                   
                })
                .then(navigate('/page2'))

        }
    }

    return (
        <div className="registerAcademyForm">
            <br/>
            <h2> Enter your login details!</h2>
            <br/>
            <form onSubmit={handleSubmit}>
                <div class="form-group" >
                    <label for="name">Username</label>
                    <input type="text" class="form-control" value={name} onChange={event => setName(event.target.value)}  placeholder="Enter username"/>
                    {invalidName && <span className="error">Username cannot be blank</span>}<br/>
                </div>
                <div class="form-group">
                    <label for="address">Password</label>
                    <input type="password" class="form-control" placeholder="Full Address" name="address" value={address} onChange={event => setAddress(event.target.value)} />
                    {invalidAddress && <span className="error">Address cannot be blank</span>}<br />
                </div>
                    <button type="submit" class="btn btn-primary">Submit</button>
            </form>
        </div>

    )
}

export default RegisterAcademy
