import {useState, useEffect} from 'react'
import AcademyCardList from './AcademyCardList'
import axios from 'axios'
import {Link} from 'react-router-dom'



const SelectFromList = (props) => {

    const api = "http://localhost:8088/api/v1/academy/"
    const api2 = "http://localhost:8088/api/v1/stream/"
    const [academyList, setAcademyList] = useState([])
    const [streamList, setStreamList] = useState([])

    useEffect(() => {
        loadAcademies()
        loadStreams()
    }, [])

    const loadAcademies = () => {
        axios.get(api)
                .then(response => {setAcademyList(response.data)})
                .catch(error => console.log('unable to load data'))
    }

    const loadStreams = () => {
        axios.get(api2)
                .then(response => {setStreamList(response.data)})
                .catch(error => console.log('unable to load data'))
    }

    return(
        <div>
            {
                academyList.map(
                    academy => <AcademyCardList key={academy.id} academy={academy}/>
                )
            }

            {
                streamList.map(
                    stream => <StreamCardList key={stream.id} stream={stream}/>
                )
            }

           <br/>
       
        </div>
    )
}

export default SelectFromList
