import {useState} from 'react'
import RandomNumberComponent from './RandomNumberComponent';
import '../styles/GameComponent.css'

const GameComponent = () => {

    const [previousNumber, setPreviousNumber] = useState(0)
    const [message, setMessage] = useState("")
    const [prediction, setPrediction] = useState("")

    const buttonClicked = (choice) => {
        setPrediction(choice)
        setMessage("Your prediction is: "+choice+". Click Generate Random Number to find out if you are correct!")
        setMessage("Your prediction is: "+choice+". Click Generate Random Number to find out if you are correct!")
    }

    const checkPrediction = (randomNumber) => {
        console.log("random number is "+randomNumber+", previous number is "+previousNumber)
        if (randomNumber > previousNumber && prediction === "higher"){
            setMessage("Congratulations!! You guessed correctly!!")
        } 
        else if (randomNumber < previousNumber && prediction === "lower"){
            setMessage("Congratulations!! You guessed correctly!!")
        }
        else {
            setMessage("You are wrong! Idiot!")
        }
        setPreviousNumber(randomNumber)
    }

    return(
        <div class='higherOrLower'>
                <RandomNumberComponent passNumberToGame={checkPrediction}/>
                <h1>Will the next number be: Higher or Lower?</h1>
                <button onClick={() => buttonClicked("Higher")}>Higher</button>
                <button onClick={() => buttonClicked("Lower")}>Lower</button>
                <br/>
                <h3>{message}</h3>
        </div>
    )
}

export default GameComponent
