import axios from 'axios'
import {useState} from 'react'
import {useNavigate} from 'react-router-dom'
import '../styles/RegisterProducts.css'

const RegisterProduct = () => {

    const api = "http://localhost:8088/api/v1/products/"
    const navigate = useNavigate()
    const [name, setName] = useState("")
    const [description, setDescription] = useState("")
    const [price, setPrice] = useState("")
    const [invalidName,setInvalidName] = useState(false)
    const [invalidDescription,setInvalidDescription] = useState(false)
    const [invalidPrice,setInvalidPrice] = useState(false)

    const handleSubmit = (event) => {
        event.preventDefault()
        if (name === ""){
            setInvalidName(true)
        } else {
            setInvalidName(false)
        }

        if (description === ""){
            setInvalidDescription(true)
        } else {
            setInvalidDescription(false)
        }

        if (price < 0){
            setInvalidPrice(true)
        } else {
            setInvalidPrice(false)
        }

        if (! invalidName && ! invalidPrice && ! invalidDescription){
             axios.post(api,
                {
                   name : name,
                   description : description,
                   price : price
                })
                .then(navigate('/page8'))
        }
    }

    return (
        <div className = 'productConditionals'>
            <form className = 'productForm' onSubmit={handleSubmit}>
            <h1 className='productTitle'>Enter product details:</h1>
                <label className='productLabel'>Name:</label>
                <input className='productInput' type="text" name="name" value={name} onChange={event => setName(event.target.value)}/>
                {invalidName && <span className="error">Name cannot be blank</span>}<br/>
                <label className='productLabel'>Description:</label>
                <input className='productInput' type="text" name="description" value={description} onChange={event => setDescription(event.target.value)}/>
                {invalidDescription && <span className="error">Description cannot be blank</span>}<br/>
                <label className='productLabel'>Price:</label>
                <input className='productInput' type="number" name="price" value={price} onChange={event => setPrice(event.target.value)}/>
                {invalidPrice && <span className="error">Price cannot be negative</span>}<br/>
                <button className='productSubmit'>Submit</button>
            </form>
        </div>

    )
}
export default RegisterProduct
