import {useState, useEffect} from 'react'
import TrainerCard from './TrainerCard'
import axios from 'axios'



const ListTrainers = () => {

    const api = "http://localhost:8088/api/v1/trainer/"
    const [trainerList, setTrainerList] = useState([])

    useEffect(() => {
        loadTrainers()
    }, [])

    const loadTrainers = () => {
        axios.get(api)
                .then(response => {setTrainerList(response.data)})
                .catch(error => console.log('unable to load data'))
    }

    return(
        <div>
            {
                trainerList.map(
                    trainer => <TrainerCard key={trainer.id} trainer={trainer}/>
                )
            }
           
        </div>
    )
}

export default ListTrainers
