import { useState } from "react"

const RegisterProduct = (props) => {
    const [name, setName] = useState("")
    const [description, setDescription] = useState("")
    const [price, setPrice] = useState("")

    const handleSubmit = (event) => {
        event.preventDefault()
    }

    return (
        <div>
            <h1>Enter product details:</h1>
            <form onSubmit={handleSubmit}>
                <label>Name:</label>
                <input type="text" name="name" value={name} onChange={event => setName(event.target.value)}/>
                {invalidName && <span className="error">Name cannot be blank</span>}<br/>
                <label>Description:</label>
                <input type="text" name="description" value={description} onChange={event => setDescription(event.target.value)}/>
                {invalidDescription && <span className="error">Description cannot be blank</span>}<br/>
                <label>Price:</label>
                <input type="number" name="price" value={price} onChange={event => setPrice(event.target.value)}/>
                {invalidPrice && <span className="error">Price cannot be negative</span>}<br/>
                <button>Submit</button>
            </form>
        </div>

    )
}
export default RegisterProduct
