import { useState } from 'react'

const Calculator = () => {
    const [value1, setValue1] = useState(0)
    const [value2, setValue2] = useState(0)
    const [result, setResult] = useState("")

    const calculate = (event) => {
        let num1 = Number(value1)
        let num2 = Number(value2)

        let finalResult = num1 + num2
        setResult(value1 + " + " + value2 + " = " + finalResult)

        event.preventDefault()
    }

    const resetPage = (event) => {
        event.preventDefault()
        setResult("")
        setNum1(0)
        setNum2(0)
    }

    return (
        <div className='calculator'>
            <form onSubmit={calculate}>
                <input type='number' name='value1' value={value1} />
                <span> + </span>
                <input type='number' name='value1' value={value2} />
            </form>
            <button>Calculate</button>
        </div>

    )
}
export default Calculator
