import axios from 'axios'
import {useState} from 'react'
import {useNavigate} from 'react-router-dom'
import '../styles/RegisterAcademy.css'

const RegisterAcademy = () => {

    const api = "http://localhost:8088/api/v1/academy/"
    const navigate = useNavigate()
    const [name, setName] = useState("")
    const [address, setAddress] = useState("")
    const [invalidName,setInvalidName] = useState(false)
    const [invalidAddress,setInvalidAddress] = useState(false)
    const [AddressAlreadyExists,setAddressAlreadyExists] = useState(false)
 

    const handleSubmit = (event) => {
        event.preventDefault()
        if (name === ""){
            setInvalidName(true)
        } else {
            setInvalidName(false)
        }

        if (address === ""){
            setInvalidAddress(true)
        } else {
            setInvalidAddress(false)
        }

        if (address === address){
            setAddressAlreadyExists(true)
        } else {
            setAddressAlreadyExists(false)
        }



        if (! invalidName && ! invalidAddress && ! AddressAlreadyExists ){
             axios.post(api,
                {
                   name : name,
                   address : address
                })
                .then(navigate('/page2'))
        }
    }

    return(
        <div>
            <h1>Enter academy details:</h1>
            <form onSubmit={handleSubmit}>
                <label>Academy Name:</label>
                <input type="text" name="name" value={name} onChange={event => setName(event.target.value)}/>
                {invalidName && <span className="error">Name cannot be blank</span>}<br/>
                <label>Address:</label>
                <input type="text" name="address" value={address} onChange={event => setAddress(event.target.value)}/>
                {invalidAddress && <span className="error">Address cannot be blank</span>}<br/>
                {AddressAlreadyExists && <span className="error">Address already exists in this location</span>}<br/>
                <button>Submit</button>
            </form>
        </div>

    )
}

export default RegisterAcademy
