import {useState, useEffect} from 'react'
import AcademyCardList from './AcademyCardList'
import axios from 'axios'
import {Link} from 'react-router-dom'



const SelectFromList = (props) => {

    props.academy = "http://localhost:8088/api/v1/academy/"
    props.stream = "http://localhost:8088/api/v1/stream/"
    const [academyList, setAcademyList] = useState([])
    const [streamList, setStreamList] = useState([])
    const {academy, stream} = props.api
    const api = "http://localhost:8088/api/v1/academy/"

    useEffect(() => {
        loadAPI()
        
    }, [])

    const loadAPI = () => {
        axios.get(api.academy)
                .then(response => {setAcademyList(response.data)})
                .catch(error => console.log('unable to load data'))
    }



    return(
        <div>
            {
                academyList.map(
                    academy => <AcademyCardList key={academy.id} academy={academy}/>
                )
            }
           <br/>
            

        </div>
    )
}

export default SelectFromList
