import '../styles/TraineeCard.css'
import {Link} from 'react-router-dom'
import {useState, useEffect} from 'react'
import AcademyCardList from './AcademyCardList'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'
import { useParams } from 'react-router-dom';


const TraineeCard = (props) => { 

    const navigate = useNavigate()
    const {id, name, stream, academy} = props.trainee
    const api = "http://localhost:8088/api/v1/trainee/"
    const  params = useParams;
    const [examResults, setExamResults] = useState([])


    const handleSubmit = (event) => {
        axios.get(api + '/' + params.trainee_id)
                .then(response => {setExamResults(response.data)})
                .catch(error => console.log('unable to load data'))

            axios.post(api,
                {
                    name: name,
                    stream: stream,
                    academy: academy,
                    
                })
                .then(navigate('/page4'))
        }
    
    
   return(
        <div className="traineeCard">
                <h2>{name}</h2>
            <div className="traineeDetails">
                <span className="miniTitle"> Stream:  </span>
                <span className='details'> {stream.name}</span> <br/>
                <span className="miniTitle"> Academy:  </span>
                <span className='details'> {academy.name}</span> <br/>
                
                <button onClick={handleSubmit} type="button" class="btn btn-dark">Exam Results</button>
            </div>
           
        </div>

    )
}

export default TraineeCard
